fun main() {
    val input = arrayOf(
        "mjqjpqmgbljsphdztnvjfqwrcgsmlb",
        "bvwbjplbgvbhsrlpgdmjqwftvncz",
        "nppdvjthqldpwncqszvftbrmjlhg",
        "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg",
        "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw",
        ""
    )

    input.forEach {
        println("Start of packet: ${getStartOfMarker(it, 4)}, Start of message: ${getStartOfMarker(it, 14)}")
    }
}

fun getStartOfMarker(input: String, numberOfDistinctChars: Int): Int {
    for (i in 0 until input.length) {
        val endIndex = i + numberOfDistinctChars
        val sequenceSet = input.substring(i, i + numberOfDistinctChars)
            .toSet()
        if (sequenceSet.size == numberOfDistinctChars) {
            return endIndex
        }
    }
    return -1
}
